```const ProductList: React.FC = () => {
  // –°–æ—Å—Ç–æ—è–Ω–∏–µ –≤—ã–±—Ä–∞–Ω–Ω–æ–π –∫–∞—Ç–µ–≥–æ—Ä–∏–∏. –ù–∞—á–∞–ª—å–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ "All" (–≤—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏)
  const [selectedCategory, setSelectedCategory] = useState<string>('All');
  // –°–æ—Å—Ç–æ—è–Ω–∏–µ –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ —Ç–∏–ø–∞ —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏: "asc" (–ø–æ –≤–æ–∑—Ä–∞—Å—Ç–∞–Ω–∏—é) –∏–ª–∏ "desc" (–ø–æ —É–±—ã–≤–∞–Ω–∏—é)
  const [sortOption, setSortOption] = useState<'asc' | 'desc'>('asc');

  // –ü–æ–ª—É—á–∞–µ–º —Å–ø–∏—Å–æ–∫ —É–Ω–∏–∫–∞–ª—å–Ω—ã—Ö –∫–∞—Ç–µ–≥–æ—Ä–∏–π, –≤–∫–ª—é—á–∞—è –≤–∞—Ä–∏–∞–Ω—Ç "All"
  const categories = ['All', ...Array.from(new Set(initialProducts.map(product => product.category)))];

  // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è —Ç–æ–≤–∞—Ä–æ–≤ –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç –≤—ã–±—Ä–∞–Ω–Ω–æ–π –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
  const filteredProducts = initialProducts.filter(product =>
    selectedCategory === 'All' ? true : product.category === selectedCategory
  );

  // –ö–æ–ø–∏—Ä—É–µ–º –æ—Ç—Ñ–∏–ª—å—Ç—Ä–æ–≤–∞–Ω–Ω—ã–π –º–∞—Å—Å–∏–≤ –∏ —Å–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ —Ü–µ–Ω–µ
  const sortedProducts = [...filteredProducts].sort((a, b) => {
    return sortOption === 'asc' ? a.price - b.price : b.price - a.price;
  });

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –∏–∑–º–µ–Ω–µ–Ω–∏—è –∑–Ω–∞—á–µ–Ω–∏–π —Å–µ–ª–µ–∫—Ç–æ–≤
  const handleCategoryChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedCategory(e.target.value);
  };

  const handleSortChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const value = e.target.value as 'asc' | 'desc';
    setSortOption(value);
  };```
``` return (
