```function isAnagramm(word1, word2) {
    if (word1 === word2) return 1

    if (word1.length !== word2.length) return 0

    const s1 = word1.toLowerCase()
    const s2 = word2.toLowerCase()

    const counter = {}

    for (let i = 0; i < s1.length; i++) {
        const char = s1.charAt(i)

        if (!counter[char]) {
            counter[char] = 1
        } else {
            counter[char]++
        }
    }

    for (let i = 0; i < s2.length; i++) {
        const char = s2.charAt(i)

        if (counter[char]) {
            counter[char]--
        } else {
            return 0
        }
    }

    return 1
}

