class EventEmitter {
    constructor () {
        this.events = {};
    }

    on(event, cb) {
        if(!this.events[event]) {
            this.events[event] = [];
        }

        this.events[event].push(cb);
        return this;
    };

    off(event, cb) {
        if(!this.events[event]) return this;
        this.events[event] = this.events[event].filter(item => return item !== cb);
        return this;
    }

    emit(event, ...args) {
        if(!this.events[event]) return this;
        this.events[event].forEach(cb => cb(...args));
        return this;
    }
}

