```function fetchWithRetries (url) {
  let retries = 3;
  return new Promise((resolve, reject) => {

    const _fetch = () => {
      fetch(url).then((res) => {
        if (!res.ok) throw new Error("–ó–∞–∫–æ–Ω—á–∏–ª–∏—Å—å –ø–æ–ø—ã—Ç–∫–∏");
