```
function findChampions(statistics) {
  const stepsByUser = new Map();
  const daysByUser = new Map();
  const allDays = new Set();

  for (const { userId, steps, day } of statistics) {
    allDays.add(day);

    stepsByUser.set(userId, (stepsByUser.get(userId) || 0) + steps);

    if (!daysByUser.has(userId)) {
      daysByUser.set(userId, new Set());
    }

    daysByUser.get(userId).add(day);
  }

  const requireDays = allDays.size;
  const validUsers = [];

  for (const [userId, day] of daysByUser.entries()) {
    if (day.size === requireDays) {
      validUsers.push(userId);
    }
  }

  let maxSteps = 0;

  const champions = [];

  for (const userId of validUsers) {
    const steps = stepsByUser.get(userId);
    if (steps > maxSteps) {
      maxSteps = steps;
      champions.length = 0;
      champions.push(userId);
    } else if (steps === maxSteps) {
      champions.push(userId);
    }
  }

  return { userId: champions, steps: maxSteps };
}

