```const promiseAll = (proms) => {
  const result = [];
  let count = 0;

  return new Promise((resolve, reject) => {
    proms.forEach((pr, idx) => {
      pr.then((res) => {
        result[idx] = res;
        count += 1;
        if (count === proms.length) resolve(result);
      }).catch(reject);
    });
  });
};

