```function printDirectoryStructure(data) {
  // –í—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –¥–ª—è —Ä–∞—Å—á–µ—Ç–∞ —Ä–∞–∑–º–µ—Ä–∞ –¥–∏—Ä–µ–∫—Ç–æ—Ä–∏–π
  function calculateSize(node) {
    if (!node.children) {
      return node.size || 0; // –î–ª—è —Ñ–∞–π–ª–æ–≤ –ø—Ä–æ—Å—Ç–æ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–∞–∑–º–µ—Ä
    }

    let totalSize = 0;
    for (const child of node.children) {
      totalSize += calculateSize(child); // –†–µ–∫—É—Ä—Å–∏–≤–Ω–æ —Å—á–∏—Ç–∞–µ–º —Ä–∞–∑–º–µ—Ä –¥–ª—è –≤—Å–µ—Ö –ø–æ—Ç–æ–º–∫–æ–≤
    }

    return totalSize;
  }

  // –°–Ω–∞—á–∞–ª–∞ —Ä–∞—Å—Å—á–∏—Ç—ã–≤–∞–µ–º —Ä–∞–∑–º–µ—Ä—ã –¥–ª—è –≤—Å–µ—Ö –ø–∞–ø–æ–∫
  const rootSize = calculateSize(data);

  // –ó–∞—Ç–µ–º –≤—ã–≤–æ–¥–∏–º —Å—Ç—Ä—É–∫—Ç—É—Ä—É
  const stack = [{ node: data, indent: '', size: rootSize }];

  while (stack.length > 0) {
    const { node, indent, size } = stack.pop();
    const isFolder = node.children && Array.isArray(node.children);

    if (isFolder) {
      console.log(`${indent}${node.name} (${size} bytes)`);

      // –î–æ–±–∞–≤–ª—è–µ–º –¥–µ—Ç–µ–π –≤ —Å—Ç–µ–∫ –≤ –æ–±—Ä–∞—Ç–Ω–æ–º –ø–æ—Ä—è–¥–∫–µ –¥–ª—è –ø—Ä–∞–≤–∏–ª—å–Ω–æ–≥–æ –≤—ã–≤–æ–¥–∞
      for (let i = node.children.length - 1; i >= 0; i--) {
        const child = node.children[i];
        const childSize = calculateSize(child);
        stack.push({ 
          node: child, 
          indent: indent + '  ',
          size: childSize
        });
      }
    } else {
      console.log(`${indent}${node.name} (${node.size || 0} bytes)`);
    }
  }

  return rootSize; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –æ–±—â–∏–π —Ä–∞–∑–º–µ—Ä —Å—Ç—Ä—É–∫—Ç—É—Ä—ã
}```

