class EventEmitter {
    constructor() {
        this.events = {}
    }

    on(event, cb) {
        if (!this.events[event]) {
            this.events[event] = []
        }

        this.events[event].push(cb)
        return this
    }

    off(event, cb) {
        if (!this.events[event]) return this
        this.events[event] = this.events[event].filter(item => item !== cb)
        return this
    }

    emit(event, ...args) {
        if (!this.events[event]) return this
        this.events[event].forEach((cb) => {
            try {
                cb(...args)
            } catch (e) {
                console.error(e)
            }
        })
        return this
    }
}


