```function compress(numbers) {
  if (numbers.length === 0) return '';

  // –°–æ—Ä—Ç–∏—Ä—É–µ–º —á–∏—Å–ª–∞
  const sorted = [...numbers].sort((a, b) => a - b);

  const ranges = [];
  let start = sorted[0];
  let end = start;

  for (let i = 1; i < sorted.length; i++) {
    const current = sorted[i];

    // –ï—Å–ª–∏ —Ç–µ–∫—É—â–µ–µ —á–∏—Å–ª–æ —Å–ª–µ–¥—É–µ—Ç –∑–∞ –ø—Ä–µ–¥—ã–¥—É—â–∏–º, —Ä–∞—Å—à–∏—Ä—è–µ–º –¥–∏–∞–ø–∞–∑–æ–Ω
    if (current === end + 1) {
      end = current;
    } else {
      // –ò–Ω–∞—á–µ –∑–∞–∫—Ä—ã–≤–∞–µ–º —Ç–µ–∫—É—â–∏–π –¥–∏–∞–ø–∞–∑–æ–Ω –∏ –Ω–∞—á–∏–Ω–∞–µ–º –Ω–æ–≤—ã–π
      if (start === end) {
        ranges.push(`${start}`);
      } else {
        ranges.push(`${start}-${end}`);
      }

      start = current;
      end = current;
    }
  }

  // –î–æ–±–∞–≤–ª—è–µ–º –ø–æ—Å–ª–µ–¥–Ω–∏–π –¥–∏–∞–ø–∞–∑–æ–Ω
  if (start === end) {
    ranges.push(`${start}`);
  } else {
    ranges.push(`${start}-${end}`);
  }

  return ranges.join(',');
}```

