```function parallelLimit(urls, limit, callback) {
  const cache = new Map();
  const results = new Array(urls.length);
  let active = 0;
  let index = 0;

  function next() {
    if (index >= urls.length && active === 0) {
      callback(results);
      return;
    }

    while (active < limit && index < urls.length) {
      const i = index;
      const url = urls[i];
      index++;
      active++;

      let fetchPromise;
      if (cache.has(url)) {
        fetchPromise = cache.get(url);
      } else {
        fetchPromise = mockFetch(url); // –∑–∞–º–µ–Ω–∏—Ç–µ –Ω–∞ fetch(url).then(res => res.text()) –¥–ª—è —Ä–µ–∞–ª—å–Ω–æ–≥–æ –∑–∞–ø—Ä–æ—Å–∞
        cache.set(url, fetchPromise);
      }

      fetchPromise
        .then((res) => {
          results[i] = res;
        })
        .finally(() => {
          active--;
          next();
        });
    }
  }

  next();
}```

