function retryPromise(fn, retries, delay) {
  return new Promise((resolve, reject) => {
    function attempt() {
      fn()
        .then(resolve)
        .catch((error) => {
          if (retries <= 0) {
            reject(error);
          } else {
            retries--;
            setTimeout(attempt, delay);
          }
        });
    }
    attempt();
  });
}

