                        <p className={styles.title}>{title}</p>
                    </div>
                    {children}
                </div>
            ) : (
                <div className={styles.button} onClick={handleToggle}>
                    <div className={styles.iconPlus}>
                        <IconPlus />
                    </div>
                    <p className={styles.title}>{title}</p>
                </div>
            )}
        </div>
    );
};

