const nominals = [5000, 1000, 500];

/**
 * ATM
 *
 * @param {number} amount
 * @returns {string[]} Returns format ['Nx3', 'Nx4', 'Nx1']
 */
function atm(amount) {
    const res = [];
    let rest = amount;

    for(let i = nominals.length - 1; i >= 0; i--) {
        const curNominal = nominals[i]
        const count = Math.floor(rest/curNominal);
        if(count > 0) {
            res.push(`${curNominal}x${count}`)
            rest -= curNominal * count
        }
    }

    return res
}

