function memoize(fn) {
    const cache = new Map(); // –•—Ä–∞–Ω–∏–ª–∏—â–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤

    return function(...args) {
        const key = JSON.stringify(args); // –£–Ω–∏–∫–∞–ª—å–Ω—ã–π –∫–ª—é—á –¥–ª—è –∞—Ä–≥—É–º–µ–Ω—Ç–æ–≤

        if (cache.has(key)) {
            return cache.get(key); // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –∫–µ—à–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç
        }

        const result = fn(...args); // –í—ã—á–∏—Å–ª—è–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
        cache.set(key, result); // –°–æ—Ö—Ä–∞–Ω—è–µ–º –≤ –∫–µ—à
        return result;
    };
}

