```function findRelativeRanks(score) {
    // –°–æ–∑–¥–∞–µ–º –∫–æ–ø–∏—é –º–∞—Å—Å–∏–≤–∞ –∏ —Å–æ—Ä—Ç–∏—Ä—É–µ–º –≤ –ø–æ—Ä—è–¥–∫–µ —É–±—ã–≤–∞–Ω–∏—è
    const sorted = [...score].sort((a, b) => b - a);

    // –°–æ–∑–¥–∞–µ–º —Å–ª–æ–≤–∞—Ä—å –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –º–µ—Å—Ç
    const rankMap = {};

    // –ó–∞–ø–æ–ª–Ω—è–µ–º —Å–ª–æ–≤–∞—Ä—å: –∫–ª—é—á - —Ä–µ–∑—É–ª—å—Ç–∞—Ç, –∑–Ω–∞—á–µ–Ω–∏–µ - –º–µ—Å—Ç–æ
    for (let i = 0; i < sorted.length; i++) {
        rankMap[sorted[i]] = i + 1; // +1 –ø–æ—Ç–æ–º—É —á—Ç–æ –º–µ—Å—Ç–∞ –Ω–∞—á–∏–Ω–∞—é—Ç—Å—è —Å 1
    }

    // –°–æ–∑–¥–∞–µ–º –º–∞—Å—Å–∏–≤ –¥–ª—è —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
    const result = [];

    // –ü—Ä–æ—Ö–æ–¥–∏–º –ø–æ –∏—Å—Ö–æ–¥–Ω–æ–º—É –º–∞—Å—Å–∏–≤—É –∏ —Ñ–æ—Ä–º–∏—Ä—É–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
    for (let num of score) {
        const rank = rankMap[num];
        if (rank === 1) {
            result.push("Gold Medal");
        } else if (rank === 2) {
            result.push("Silver Medal");
        } else if (rank === 3) {
            result.push("Bronze Medal");
        } else {
            result.push(rank.toString());
        }
    }

    return result;
}

