    .filter(child => child !== null); // –£–¥–∞–ª—è–µ–º —É–∑–ª—ã, –∫–æ—Ç–æ—Ä—ã–µ —Å—Ç–∞–ª–∏ null

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –¥–æ–ª–∂–µ–Ω –ª–∏ —Å–∞–º —É–∑–µ–ª –æ—Å—Ç–∞—Ç—å—Å—è –≤ –¥–µ—Ä–µ–≤–µ
  if (!rootNode.alive && rootNode.children.length === 0) {
