 * @param {number|null} time –ò–Ω—Ç–µ—Ä–≤–∞–ª –≤ –º–∏–ª–ª–∏—Å–µ–∫—É–Ω–¥–∞—Ö. –ï—Å–ª–∏ null, —Ç–æ actions –Ω–µ –≤—ã–ø–æ–ª–Ω—è—é—Ç—Å—è.
 */
const useIntervalActions = (actions, time) => {
  const savedActions = useRef();

  // –ó–∞–ø–æ–º–∏–Ω–∞–µ–º –ø–æ—Å–ª–µ–¥–Ω–∏–µ actions.
  useEffect(() => {
    savedActions.current = actions;
  }, [actions]);

  useEffect(() => {
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ time —è–≤–ª—è–µ—Ç—Å—è —á–∏—Å–ª–æ–º –∏ –±–æ–ª—å—à–µ –Ω—É–ª—è.
    if (typeof time === 'number' && time > 0) {
      // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è actions.
      const tick = () => {
        const currentActions = savedActions.current; // –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—É—â–∏–µ actions

        if (Array.isArray(currentActions)) {
          currentActions.forEach(action => {
            if (typeof action === 'function') {
              action();
            } else {
              console.warn('useIntervalActions: –ù–µ–≤–µ—Ä–Ω—ã–π —Ç–∏–ø –¥–µ–π—Å—Ç–≤–∏—è. –û–∂–∏–¥–∞–µ—Ç—Å—è —Ñ—É–Ω–∫—Ü–∏—è.');
            }
          });
        } else if (typeof currentActions === 'function') {
          currentActions();
        } else {
          console.warn('useIntervalActions: –ù–µ–≤–µ—Ä–Ω—ã–π —Ç–∏–ø actions. –û–∂–∏–¥–∞–µ—Ç—Å—è —Ñ—É–Ω–∫—Ü–∏—è –∏–ª–∏ –º–∞—Å—Å–∏–≤ —Ñ—É–Ω–∫—Ü–∏–π.');
        }
      };

      // –ó–∞–ø—É—Å–∫–∞–µ–º –∏–Ω—Ç–µ—Ä–≤–∞–ª.
      const intervalId = setInterval(tick, time);

      // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ—á–∏—Å—Ç–∫–∏ –∏–Ω—Ç–µ—Ä–≤–∞–ª–∞ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞ –∏–ª–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ time.
      return () => clearInterval(intervalId);
    }

    // –ï—Å–ª–∏ time –Ω–µ —è–≤–ª—è–µ—Ç—Å—è —á–∏—Å–ª–æ–º –∏–ª–∏ –º–µ–Ω—å—à–µ –∏–ª–∏ —Ä–∞–≤–Ω–æ –Ω—É–ª—é, –Ω–∏—á–µ–≥–æ –Ω–µ –¥–µ–ª–∞–µ–º.
    return () => {}; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –ø—É—Å—Ç—É—é —Ñ—É–Ω–∫—Ü–∏—é –æ—á–∏—Å—Ç–∫–∏, —á—Ç–æ–±—ã –∏–∑–±–µ–∂–∞—Ç—å –ø—Ä–µ–¥—É–ø—Ä–µ–∂–¥–µ–Ω–∏–π.
  }, [time]);
};

