```type Points = Array<[number, number]>;

const getBoundingBoxArea = (points: Points): number => {
    if (points.length === 0) return 0;

    let minX = points[0][0];
    let maxX = points[0][0];
    let minY = points[0][1];
    let maxY = points[0][1];

    for (const [x, y] of points) {
        if (x < minX) minX = x;
        if (x > maxX) maxX = x;
        if (y < minY) minY = y;
        if (y > maxY) maxY = y;
    }

    return (maxX - minX) * (maxY - minY);
};

const points: Points = [
    [1, 1],
    [2, 3],
    [5, 5]
];

const points2: Points = [
    [-18, -18],
    [23, 13],
    [22, 33],
    [2, 3],
    [30, 50]
];

console.log(getBoundingBoxArea(points)); // –î–æ–ª–∂–Ω–æ –≤—ã–≤–µ—Å—Ç–∏ 16
console.log(getBoundingBoxArea(points2)); // –î–æ–ª–∂–Ω–æ –≤—ã–≤–µ—Å—Ç–∏ 3600```

