  }

  off(event, callback){
    if(this.events[event]){
      this.events[event] = this.events[event].filter(fn => fn !== callback)
    }
  }

  trigger(event) {
    if (this.events[event]) {
      this.events[event].forEach(callback => callback());
    }
  }
}```


