```
function printDirectoryStructure(data) {
    const stack = [{ item: data, level: 0 }];

    while (stack.length > 0) {
        const { item, level } = stack.pop();

        // Print current item with proper indentation
        console.log('  '.repeat(level) + item.name);

        // If the item has children, add them to stack in reverse order
        // to maintain correct output order (since we're using stack)
        if (item.children) {
            for (let i = item.children.length - 1; i >= 0; i--) {
                stack.push({
                    item: item.children[i],
                    level: level + 1
                });
            }
        }
    }
}```

–†–µ—à–µ–Ω–∏–µ:

```function typeWriter(delay, outputChar) {
  // –û—á–µ—Ä–µ–¥—å —Ç–µ–∫—Å—Ç–æ–≤, –∫–æ—Ç–æ—Ä—ã–µ –Ω—É–∂–Ω–æ –Ω–∞–ø–µ—á–∞—Ç–∞—Ç—å –ø–æ –ø–æ—Ä—è–¥–∫—É
  const queue = [];
  let isPrinting = false; // –§–ª–∞–≥, –∏–¥—ë—Ç –ª–∏ —Å–µ–π—á–∞—Å –ø–µ—á–∞—Ç—å

  // –§—É–Ω–∫—Ü–∏—è, –∫–æ—Ç–æ—Ä–∞—è –±–µ—Ä—ë—Ç —ç–ª–µ–º–µ–Ω—Ç –∏–∑ –æ—á–µ—Ä–µ–¥–∏ –∏ –ø–µ—á–∞—Ç–∞–µ—Ç
  function processQueue() {
    if (queue.length === 0) {
      // –û—á–µ—Ä–µ–¥—å –ø—É—Å—Ç–∞ ‚Äî –∑–∞–∫–∞–Ω—á–∏–≤–∞–µ–º
      isPrinting = false;
      return;
    }
    isPrinting = true;

    // –ë–µ—Ä—ë–º –ø–µ—Ä–≤—ã–π —ç–ª–µ–º–µ–Ω—Ç (—Å—Ç—Ä–æ–∫—É) –∏–∑ –æ—á–µ—Ä–µ–¥–∏
    const text = queue.shift();
    let index = 0;

    // –í–Ω—É—Ç—Ä–µ–Ω–Ω—è—è —Ä–µ–∫—É—Ä—Å–∏–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ—Å–∏–º–≤–æ–ª—å–Ω–æ–π –ø–µ—á–∞—Ç–∏
    function printNext() {
      if (index < text.length) {
        outputChar(text[index]);
        index++;
        setTimeout(printNext, delay);
      } else {
        // –ö–æ–≥–¥–∞ –∑–∞–∫–æ–Ω—á–∏–ª–∏ —Ç–µ–∫—É—â—É—é —Å—Ç—Ä–æ–∫—É ‚Äî –±–µ—Ä—ë–º —Å–ª–µ–¥—É—é—â—É—é
        processQueue();
      }
    }

    // –°—Ç–∞—Ä—Ç—É–µ–º –ø–µ—á–∞—Ç—å —Ç–µ–∫—É—â–µ–π —Å—Ç—Ä–æ–∫–∏
    printNext();
  }

  // –í–æ–∑–≤—Ä–∞—â–∞–µ–º–∞—è —Ñ—É–Ω–∫—Ü–∏—è, –∫–æ—Ç–æ—Ä—É—é –±—É–¥–µ–º –≤—ã–∑—ã–≤–∞—Ç—å –¥–ª—è –ø–µ—á–∞—Ç–∏ —Å—Ç—Ä–æ–∫–∏
  return function writeText(text) {
    // –î–æ–±–∞–≤–ª—è–µ–º –Ω–æ–≤—É—é —Å—Ç—Ä–æ–∫—É –≤ –æ—á–µ—Ä–µ–¥—å
    queue.push(text);

    // –ï—Å–ª–∏ –≤ –¥–∞–Ω–Ω—ã–π –º–æ–º–µ–Ω—Ç –Ω–∏—á–µ–≥–æ –Ω–µ –ø–µ—á–∞—Ç–∞–µ—Ç—Å—è ‚Äî –∑–∞–ø—É—Å–∫–∞–µ–º –ø—Ä–æ—Ü–µ—Å—Å
    if (!isPrinting) {
      processQueue();
    }
  };
}```

