```const ClientList = ({options}: {options:IOtion[]}) => {
    const [filtredClients,setFiltredClients] = useState([])
    const [selectedRegion,setSelectedRegion] = useState('')
    const [isPremium, setIsPremium] = useState(false)

    useEffect(()=>{
        if(selectedRegion){
            setFiltredClients([])
            try{
            axios.get('/clients',{params:{city:selectedRegion}})
            .then(response=>setFiltredClients(response.data))
            .catch(err=>console.error(err))
            }
            catch(error){
                throw new Error(error)
            }
        }
    },[selectedRegion])

    const filterByPremium = useMemo(()=>{filtredClients.filter(client=>{
        if(isPremium) return client.isPremium
        return false
    })}, [isPremium])

    return (
        <>
        <Select name="region" options={options} onChange={(e)=>setSelectedRegion(e.target.value)} />
        <label>
        <input type='checkbox' checked = {isPremium} onChange = {(e)=> setIsPremium(e.target.checked)}/>
        –ü—Ä–µ–º–∏–∞–ª—å–Ω—ã–µ
        </label>
        <ul>
            {filterByPremium.map(client)=>(
            <li key={client.id}>
            {client.orgName} - {client.email} {client.isPremium ? 'Premium' : 'Default'}
            </li>
            )}
        </ul>
        </>
    )
 }```

