```import { useCallback, useEffect, useRef, useState } from "react";

const initialUrl = "https://rickandmortyapi.com/api/character";

const useFetchData = (url: string): [boolean, string, () => Promise<any[]>] => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const nextUrl = useRef<string | null>(null);

  const getData = useCallback(async () => {
    setError("");
    setIsLoading(true);
    try {
      const res = await fetch(nextUrl.current ? nextUrl.current : url);
      const data = await res.json();
      return data.results || [];
    } catch (err) {
      if (err instanceof Error) setError(err.message || "Unknown error");
      return [];
    } finally {
      setIsLoading(false);
    }
  }, []);

  return [isLoading, error, getData];
};

export default function App() {
  const [characters, setCharacters] = useState<any[]>([]);
  const [isLoading, error, getData] = useFetchData(initialUrl);

  useEffect(() => {
    (async () => {
      const data = await getData();
      setCharacters(data);
    })();
  }, [getData]);

  const handleLoadMore = async () => {
    const data = await getData();
    setCharacters((prev) => [...prev, ...data]);
  };

  if (isLoading) return <div>...Loading...</div>;
  if (error) return <div>There was an error: {error}</div>;

  return (
    <div>
      <CharactersList characters={characters} />
      <button onClick={handleLoadMore}>...Load more...</button>
    </div>
  );
}

function CharactersList({ characters }) {
  return (
    <div>
      {characters.map((character) => (
        <Character key={character.id} character={character} />
      ))}
    </div>
  );
}

function Character({ character }) {
  return <div>{character.name}</div>;
}```

