```
class MaxStack {
    constructor() {
        this.stack = []; // –û—Å–Ω–æ–≤–Ω–æ–π —Å—Ç–µ–∫
        this.maxStack = []; // –í—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω—ã–π —Å—Ç–µ–∫ –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –º–∞–∫—Å–∏–º—É–º–æ–≤
    }

    // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ —ç–ª–µ–º–µ–Ω—Ç–∞ –≤ —Å—Ç–µ–∫
    push(x) {
        this.stack.push(x);
        // –ï—Å–ª–∏ maxStack –ø—É—Å—Ç –∏–ª–∏ x >= —Ç–µ–∫—É—â–µ–≥–æ –º–∞–∫—Å–∏–º—É–º–∞, –¥–æ–±–∞–≤–ª—è–µ–º x –≤ maxStack
        if (this.maxStack.length === 0 || x >= this.maxStack[this.maxStack.length - 1]) {
            this.maxStack.push(x);
        }
    }

    // –£–¥–∞–ª–µ–Ω–∏–µ –∏ –≤–æ–∑–≤—Ä–∞—Ç –≤–µ—Ä—Ö–Ω–µ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞
    pop() {
        if (this.stack.length === 0) {
            throw new Error("Stack is empty");
        }
        const popped = this.stack.pop();
        // –ï—Å–ª–∏ —É–¥–∞–ª—ë–Ω–Ω—ã–π —ç–ª–µ–º–µ–Ω—Ç —Ä–∞–≤–µ–Ω —Ç–µ–∫—É—â–µ–º—É –º–∞–∫—Å–∏–º—É–º—É, —É–¥–∞–ª—è–µ–º –µ–≥–æ –∏–∑ maxStack
        if (popped === this.maxStack[this.maxStack.length - 1]) {
            this.maxStack.pop();
        }
        return popped;
    }

    // –í–æ–∑–≤—Ä–∞—Ç –≤–µ—Ä—Ö–Ω–µ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞ –±–µ–∑ —É–¥–∞–ª–µ–Ω–∏—è
    top() {
        if (this.stack.length === 0) {
            throw new Error("Stack is empty");
        }
        return this.stack[this.stack.length - 1];
    }

    // –í–æ–∑–≤—Ä–∞—Ç –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞
    getMax() {
        if (this.maxStack.length === 0) {
            throw new Error("Stack is empty");
        }
        return this.maxStack[this.maxStack.length - 1];
    }
}

