```–°–¥–µ–ª–∞—Ç—å —Ä–µ–≤—å—é –∏ —Ä–µ—Ñ–∞–∫—Ç–æ—Ä –∫–æ–¥–∞
import * as React from 'react';
import { ITennisGame, TennisGame } from "./Interfaces/ITennisGame";
import { useReducer } from "react";

export interface ITennisGame {
  wonPoint(string): void;
  getScore(): string;
}

export class TennisGame implements ITennisGame {
  private score1: number = 0;
  private score2: number = 0;
  private player1Name: string;
  private player2Name: string;

  constructor(player1Name: string, player2Name: string) {
    this.player1Name = player1Name;
    this.player2Name = player2Name;
  }

  getScore(): string {
    var score: any;
    let tempScore = 0;
    if (this.score1 == this.score2) {
      switch (this.score1) {
        case 0:
          score = "Love-ALL";
          break;
        case 1:
          score = "Fifteen-ALL";
          break;
        case 2:
          score = "Thirty-ALL";
          break;
        default:
          score = "Deuce";
          break;
      }
    } else if (this.score1 >= 4 || this.score2 >= 4) {
      var minusResult = this.score1 - this.score2;
      if (minusResult == 1) score = "Advantage player1";
      else if (minusResult == -1) score = "Advantage player2";
      else if (minusResult >= 2) score = "Win for player1";
      else score = "Win for player2";
    } else {
      for (var i = 1; i < 3; i++) {
        if (i == 1) {
          tempScore = this.score1;
        } else {
          score += "-";
          tempScore = this.score2;
        }
        switch (tempScore) {
          case 0:
            score += "Love";
            break;
          case 1:
            score += "Fifteen";
            break;
          case 2:
            score += "Thirty";
            break;
          case 3:
            score += "Forty";
            break;
        }
      }
    }
    return score;
  }

  wonPoint(playerName: string): void {
    if (playerName == "player1") this.score1 += 1;
    else this.score2 += 1;
  }
}

interface State {
  game: ITennisGame;
}

let tennisGame: ITennisGame = new TennisGame("playerName1", "playerName2");

const initialState: State = { game: tennisGame };

type CounterAction =
  | { type: "addPlayer1Point" }
  | { type: "addPlayer2Point" }
  | { type: "getScore" };

function stateReducer(state: State, action: CounterAction): State | Never {
  switch (action.type) {
    case "addPlayer1Point":
      state.game.wonPoint("firstPlayer");
      return state;
    case "addPlayer2Point":
      state.game.wonPoint("secondPlayer");
      return state;
    case "getScore":
      return state;
    default:
      throw new Error("Unknown action");
  }
}

const [state, dispatch] = useReducer(stateReducer, initialState);

const addFirstPlayerPoint = () => dispatch({ type: "addPlayer1Point" });
const addSecondPlayerPoint = () => dispatch({ type: "addPlayer2Point" });
const getScore = () => dispatch({ type: "getScore" });

export default function App() {
  return (
    <div>
      <h1>Welcome to tennis game</h1>
      <p>Count: {state.game.getScore()}</p>
      <button onClick={addFirstPlayerPoint}>add first player point</button>
      <button onClick={addSecondPlayerPoint}>add second player point</button>
      <button onClick={getScore}>get score</button>
    </div>
  );
}```

