    let a = 0; // –ü—Ä–µ–¥—ã–¥—É—â–µ–µ —á–∏—Å–ª–æ
    let b = 1; // –¢–µ–∫—É—â–µ–µ —á–∏—Å–ª–æ

    for (let i = 2; i <= n; i++) {
        const temp = a + b; // –°–ª–µ–¥—É—é—â–µ–µ —á–∏—Å–ª–æ
        a = b; // –û–±–Ω–æ–≤–ª—è–µ–º –ø—Ä–µ–¥—ã–¥—É—â–µ–µ —á–∏—Å–ª–æ
        b = temp; // –û–±–Ω–æ–≤–ª—è–µ–º —Ç–µ–∫—É—â–µ–µ —á–∏—Å–ª–æ
    }

    return a; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –ø—Ä–µ–¥—ã–¥—É—â–µ–µ —á–∏—Å–ª–æ, —Ç–∞–∫ –∫–∞–∫ —Ü–∏–∫–ª –∏–¥–µ—Ç –¥–æ n
}

// –ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è:
