// –í–æ–∑–≤—Ä–∞—â–∞—Ç—å –Ω–æ–≤—É—é —Ñ—É–Ω–∫—Ü–∏—é, –∫–æ—Ç–æ—Ä–∞—è –ø—Ä–∏ –∫–∞–∂–¥–æ–º –≤—ã–∑–æ–≤–µ –∏–∑–º–µ–Ω—è–µ—Ç start –≤ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏–∏ —Å –∑–∞–¥–∞–Ω–Ω–æ–π –æ–ø–µ—Ä–∞—Ü–∏–µ–π type –∏ –∑–Ω–∞—á–µ–Ω–∏–µ–º step.
// –ü–æ–¥–¥–µ—Ä–∂–∏–≤–∞—Ç—å –æ–ø–µ—Ä–∞—Ü–∏–∏ —Å–ª–æ–∂–µ–Ω–∏—è ("+"), –≤—ã—á–∏—Ç–∞–Ω–∏—è ("-"), —É–º–Ω–æ–∂–µ–Ω–∏—è ("*"), –¥–µ–ª–µ–Ω–∏—è ("/").
// –ì–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å –æ—à–∏–±–∫—É, –µ—Å–ª–∏ –ø–µ—Ä–µ–¥–∞–Ω –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –æ–ø–µ—Ä–∞—Ç–æ—Ä.


function createCounter(start = 0, type = '+', step = 1) {
  return function () {
    switch (type) {
      case '+':
        return start += step;
      case '-':
        return start -= step;
      case '*':
        return start *= step;
      case '/':
        return start /= step;
      default:
        throw new Error("–ù–µ–¥–æ–ø—É—Å—Ç–∏–º–∞—è –æ–ø–µ—Ä–∞—Ü–∏—è. –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ '+', '-', '*', '/'");
    }
  };
}

// –ü—Ä–∏–º–µ—Ä—ã –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è:
const counter1 = createCounter(3); // –ü–æ —É–º–æ–ª—á–∞–Ω–∏—é: type='+', step=1
console.log(counter1()); // num === 4

const counter2 = createCounter(3, '-', 3);
console.log(counter2()); // num_2 === 0

const counter3 = createCounter(3, '*', 3);
console.log(counter3()); // num_3 === 9

