// –¢—Ä–µ–±—É–µ—Ç—Å—è –Ω–∞–ø–∏—Å–∞—Ç—å —Ñ—É–Ω–∫—Ü–∏—é getPaths(items), –∫–æ—Ç–æ—Ä–∞—è –≤–µ—Ä–Ω—ë—Ç –º–∞—Å—Å–∏–≤ –≤—Å–µ—Ö –≤–æ–∑–º–æ–∂–Ω—ã—Ö –ø—É—Ç–µ–π –æ—Ç –∫–æ—Ä–Ω–µ–≤—ã—Ö —É–∑–ª–æ–≤ –¥–æ –ª–∏—Å—Ç—å–µ–≤. –ö–∞–∂–¥—ã–π –ø—É—Ç—å –¥–æ–ª–∂–µ–Ω 
// –ø—Ä–µ–¥—Å—Ç–∞–≤–ª—è—Ç—å —Å–æ–±–æ–π —Å—Ç—Ä–æ–∫—É, –≤ –∫–æ—Ç–æ—Ä–æ–π —É–∑–ª—ã —Å–æ–µ–¥–∏–Ω–µ–Ω—ã —Å–∏–º–≤–æ–ª–æ–º " -> "

function getPaths(items, path = []) {
    let result = [];

    items.forEach(item => {
        const newPath = [...path, item.value]; // –î–æ–±–∞–≤–ª—è–µ–º —Ç–µ–∫—É—â–∏–π —É–∑–µ–ª –≤ –ø—É—Ç—å

        if (item.children) {
            // –†–µ–∫—É—Ä—Å–∏–≤–Ω–æ –æ–±—Ö–æ–¥–∏–º –¥–æ—á–µ—Ä–Ω–∏–µ —ç–ª–µ–º–µ–Ω—Ç—ã
            result.push(...getPaths(item.children, newPath));
        } else {
            // –ï—Å–ª–∏ –¥–µ—Ç–µ–π –Ω–µ—Ç, –¥–æ–±–∞–≤–ª—è–µ–º –ø—É—Ç—å –≤ —Ä–µ–∑—É–ª—å—Ç–∞—Ç
            result.push(newPath.join(' -> '));
        }
    });

    return result;
}

