function logger(fn) {
  let isCalled = false; // –§–ª–∞–≥ –≤—ã–∑–æ–≤–∞ —Ñ—É–Ω–∫—Ü–∏–∏
  let result; // –ü–µ—Ä–µ–º–µ–Ω–Ω–∞—è –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞

  return function (...args) {
    if (isCalled) return undefined; // –ï—Å–ª–∏ —É–∂–µ –≤—ã–∑—ã–≤–∞–ª–∏, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º undefined
    isCalled = true; // –°—Ç–∞–≤–∏–º —Ñ–ª–∞–≥
    result = fn(...args); // –í—ã–∑—ã–≤–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é –∏ —Å–æ—Ö—Ä–∞–Ω—è–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
    return result; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –ø–µ—Ä–≤–æ–≥–æ –≤—ã–∑–æ–≤–∞
  };
}

