```'use client'

import dynamic from 'next/dynamic'
import { useSWRConfig } from 'swr'
import { useState } from 'react'

import { QuestionMarkCircleIcon } from '@heroicons/react/24/solid'
import { ArrowsPointingOutIcon, ArrowsPointingInIcon } from '@heroicons/react/24/outline'
import { Modal, ModalBody, ModalContent, SelectItem, Tooltip } from '@nextui-org/react'
import { Avatar, Button, Input, Select } from '@/components/UI/NextUI'

import { UploadFile } from './_components/UploadFile'

import { useAuth } from '@/providers/AuthProvider'

import { S3_URL } from '@/api_v2/config'
import { useCreateBlogPost, useGetBlogThemes } from '@/api_v2/blog'
import { formatName } from '@/app/helpers'

import { Jost } from 'next/font/google'
const font = Jost({ subsets: ['latin', 'cyrillic'] })

const ReactQuill = dynamic(() => import('react-quill'), { ssr: false })

import './index.css'
import 'react-quill/dist/quill.snow.css'

interface ComponentProps {
  expanded: boolean
  isOpen: boolean
  onExpandEditor: () => void
  onClose: () => void
}

const BlogPostCreateModal = ({
  // WTF
  expanded: isExpanded,
  isOpen,
  onExpandEditor,
  onClose,
}: ComponentProps) => {
  const { account } = useAuth()
  const { mutate } = useSWRConfig()

  // WTF
  const [value, setValue] = useState('')
  const [selectValue, setSelectValue] = useState<string>('')

  const [title, setTitle] = useState('')
  const [images, setImages] = useState<any>([])

  const { trigger, isMutating } = useCreateBlogPost()
  const { data } = useGetBlogThemes()

  const modules = {
    toolbar: [
      ['link'],
      ['bold', 'italic'],
      [{ header: [2, 3, false] }],
      [{ list: 'ordered' }, { list: 'bullet' }],
    ],
  }

  const selectTheme: React.ChangeEventHandler<HTMLSelectElement> = ({
    target: { value },
  }) => {
    setSelectValue(value)
  }

  const onHandleCreatePost = async () => {
    // WTF
    const themesIds = selectValue.split(',').map((item: string) => Number(item))
    const filteredThemes = data?.data.filter((theme) => themesIds.includes(theme.id))

    const body = {
      title,
      text: value,
      images,
      themes: filteredThemes,
    }

    const response = await trigger(body)

    // WTF
    if (response?.data) {
      mutate(
        (key) => typeof key === 'string' && key.startsWith('blog/post/my'),
        async (posts: any) => {
          if (posts) {
            const updatedPosts = [
              {
                ...response.data,
                stats: {
                  bookmarks: 0,
                  is_bookmarked: false,
                  is_liked: false,
                  likes: 0,
                  views: 0,
                },
              },
              ...posts.data,
            ]

            return { data: updatedPosts, count: posts.count + 1 }
          }
          return posts
        },
        { revalidate: false },
      )
      mutate(
        (key) => typeof key === 'string' && key.startsWith(`blog/${account?.id}`),
        async (data: any) => {
          if (data) {
            const updatedData = {
              user: { ...data.data.user },
              stats: {
                ...data.data.stats,
                posts: data.data.stats?.posts + 1,
              },
            }
            return { data: updatedData }
          }
          return data
        },
        { revalidate: false },
      )
      onClose()
      resetForm()
    }
  }

  const resetForm = () => {
    setValue('')
    setTitle('')
    setImages([])
    setSelectValue('')
  }```

