function withTimeout(promise, ms = 1000) {
  return new Promise((resolve, reject) => {
    const timer = setTimeout(() => {
      reject(new Error("timeout"));
    }, ms);

    promise.then(
      (value) => {
        clearTimeout(timer);
        resolve(value);
      },
      (error) => {
        clearTimeout(timer);
        reject(error);
      },
    );
  });
}

async function check(url1, url2, solution) {
  // –ó–∞–ø—É—Å–∫–∞–µ–º –æ–±–∞ –≤—ã–∑–æ–≤–∞ checkResult —Å –æ–≥—Ä–∞–Ω–∏—á–µ–Ω–∏–µ–º –≤ 1 —Å–µ–∫:
  const p1 = withTimeout(checkResult(url1, solution), 1000);
  const p2 = withTimeout(checkResult(url2, solution), 1000);

