type TreeNode<T> = {
  children?: TreeNode<T>[];
} & T;

type TreeProps<T> = {
  data: TreeNode<T>;
  getValue: (node: T) => number;
  getTitle: (node: T) => string;
};

function Tree<T>({ data, getValue, getTitle }: TreeProps<T>) {
  return (
    <ul>
      <li>
        {getValue(data)} - {getTitle(data)}
        {data.children && (
          <ul>
            {data.children.map((child, index) => (
              <Tree
                key={index}
                data={child}
                getValue={getValue}
                getTitle={getTitle}
              />
            ))}
          </ul>
        )}
      </li>
    </ul>
  );
}

