```function callLimit(fn, limit, callback) {
    let count = 0;

    const wrapped = function (...args) {
        if (count >= limit) {
            if (callback) {
                callback();
            }
            return; // –ü—Ä–µ—Ä—ã–≤–∞–µ–º –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–µ, –µ—Å–ª–∏ –ø—Ä–µ–≤—ã—à–µ–Ω –ª–∏–º–∏—Ç
        }

        count++;
        const result = fn.apply(this, args);

        if (count === limit && callback) {
            callback();
        }

        return result; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –≤—ã–∑–æ–≤–∞ `fn`
    };

    wrapped.reset = function () {
        count = 0;
        return wrapped;
    };

    return wrapped;
}

// –ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è:
const limitedFn = callLimit((x) => x * 2, 3, () => console.log("–õ–∏–º–∏—Ç –¥–æ—Å—Ç–∏–≥–Ω—É—Ç"));

console.log(limitedFn(2)); // 4
