```export class JsonCsvConverter {
  private readonly _separator: string;
  private readonly _columns: object;

  constructor(columns: object, separator: string = ',') {
    if (!columns || typeof columns !== 'object') {
      throw new Error('Columns must be a valid object.');
    }
    this._columns = columns;
    this._separator = separator;
  }

  /**
   * –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç –∑–∞–≥–æ–ª–æ–≤–∫–∏ –¥–ª—è CSV –∏–∑ –∫–ª—é—á–µ–π –∫–æ–ª–æ–Ω–æ–∫.
   */
  getHeader(): string {
    const columnKeys = Object.keys(this._columns);
    return columnKeys.join(this._separator) + '\r\n';
  }

  /**
   * –ö–æ–Ω–≤–µ—Ä—Ç–∏—Ä—É–µ—Ç –º–∞—Å—Å–∏–≤ —Å—Ç—Ä–æ–∫ –≤ —Ñ–æ—Ä–º–∞—Ç CSV.
   */
  batch(rows: any[]): string {
    if (!Array.isArray(rows)) {
      throw new Error('Input data must be an array.');
    }

    const csvRows = rows.map((row) => this.convert(row));
    return csvRows.join('');
  }

  /**
   * –ö–æ–Ω–≤–µ—Ä—Ç–∏—Ä—É–µ—Ç JSON-–æ–±—ä–µ–∫—Ç –≤ CSV-—Å—Ç—Ä–æ–∫—É.
   */
  convert(json: any = {}): string {
    if (typeof json !== 'object' || json === null) {
      throw new Error('Input must be a valid JSON object.');
    }

    const columnKeys = Object.keys(this._columns);

    return columnKeys
      .map((key) => {
        const value = this.resolveNestedValue(json, key);

        // –û–±—Ä–∞–±–æ—Ç–∫–∞ undefined/null
        if (value === undefined || value === null) {
          return '';
        }

        // –ï—Å–ª–∏ –∑–Ω–∞—á–µ–Ω–∏–µ —Å—Ç—Ä–æ–∫–∞ —Å –∑–∞–ø—è—Ç–æ–π, –æ–±–æ—Ä–∞—á–∏–≤–∞–µ–º –≤ –∫–∞–≤—ã—á–∫–∏
        if (typeof value === 'string' && value.includes(this._separator)) {
          return `"${value}"`;
        }

        return value;
      })
      .join(this._separator) + '\r\n';
  }

  /**
   * –ò–∑–≤–ª–µ–∫–∞–µ—Ç –∑–Ω–∞—á–µ–Ω–∏–µ –∏–∑ –≤–ª–æ–∂–µ–Ω–Ω—ã—Ö –æ–±—ä–µ–∫—Ç–æ–≤ –ø–æ –∫–ª—é—á—É.
   */
  private resolveNestedValue(obj: any, key: string): any {
    try {
      return key.split('.').reduce((acc, part) => {
        if (Array.isArray(acc)) {
          return acc.map((item) => item[part]).join(', ');
        }
        return acc[part];
      }, obj);
    } catch (error) {
      return undefined; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º undefined, –µ—Å–ª–∏ –∫–ª—é—á –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–µ–Ω
    }
  }
}```

