```function sum(a,b) {
  console.log('sum called')
  return a + b;
}

const memoizeOne = (func) => {
  let cache = {};
  return (...args) => {
    const key = JSON.stringify(args);
    if (cache[key]) {
      return cache[key]
    } else {
      const cache = {}
    }
    const res = func(...args);
    cache[key] = res;
    return res
  }
}

const memoSum = memoizeOne(sum);
console.log(memoSum(1,2)); // sum called 3
console.log(memoSum(1,2)); // 3
console.log(memoSum(2,3)); // sum called 5
console.log(memoSum(1,2)); // sum called 3```

