```export default () => {
    const [messages, setMessages] = useState([])
    const [message, setMessage] = useState('')

    function onChange(event) {
        setMessage(event.target.value)
    }
    function onAddMessage() {
        setMessages(prev => {
            return [...prev, {id: crypto.randomUUID(), value: message, likes: 0}]
        })
        setMessage('')
    }

    const onClickLike = useCallback((msgItemId) => {
        setMessages(prev => {
            const newMessages = prev.map(msgItem => {
                if (msgItem.id === msgItemId) {
                    return { ...msgItem, likes: msgItem.likes + 1 }
                } else {
                    return msgItem
                }
            })

            return [...newMessages]
        })
    }, [])


    const countAllLikes = messages.reduce((state, msgItem) => {
        return state = state + msgItem.likes
    }, 0) 
    return <div>
        <h1>Likes {countAllLikes}</h1>

        <ul>
            {messages.map(msgItem => {

                return (<li key={msgItem.id}>
                    {msgItem.value}
                    Count: {msgItem.likes}
                    <button onClick={() => onClickLike(msgItem.id)}>Like</button>
                </li>)
            })}
        </ul>

        <input value={message} onChange={onChange} />
        <button onClick={onAddMessage}>Send</button>
    </div>
}```

