  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        const [usersData, postsData] = await Promise.all([fetchUsers(), fetchPosts()]);

        const usersWithPosts = usersData.map(user => ({
          ...user,
          posts: postsData.filter(post => post.userId === user.id)
        }));

        setUsers(usersWithPosts);
      } catch (err) {
        console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö:", err);
        setError("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ.");
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  if (loading) {
    return <p style={{ color: "blue" }}>–ó–∞–≥—Ä—É–∑–∫–∞...</p>;
  }

  if (error) {
    return <p style={{ color: "red" }}>{error}</p>;
  }

  return (
    <div>
      <h2>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏ —Å –∏—Ö –ø–æ—Å—Ç–∞–º–∏</h2>
      {users.map((user) => (
        <div key={user.id} style={{ border: "1px solid #ddd", marginBottom: 20, padding: 10 }}>
          <UserCard id={user.id} name={user.name} username={user.username} email={user.email} />
          <h4>–ü–æ—Å—Ç—ã:</h4>
          {user.posts.length > 0 ? (
            user.posts.map((post) => <PostCard key={post.id} title={post.title} body={post.body} />)
          ) : (
            <p>–ù–µ—Ç –ø–æ—Å—Ç–æ–≤</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default Page2;```
