const str = ‚Äò–ø—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏–µ'; // "–ø—Ä–µ–≥—Ä–∏–º–º–∞—Ä–æ–≤–∏–Ω–∞–æ¬ª

function changeName(str) {
 const vowels = ['–æ', '–∞', '–∏', '–µ'];
 const letters = str.split('');
 const vowelsInWord = [];
 for (let i = 0; i < letters.length; i++) {
  if (vowels.includes(letters[i])) {
   vowelsInWord.push(letters[i]);
  }
 }
 vowelsInWord.reverse();
 let vowelIndex = 0;
 for (let i = 0; i < letters.length; i++) {
  if (vowels.includes(letters[i])) {
   letters[i] = vowelsInWord[vowelIndex];
   vowelIndex++;
  }
 }
 return letters.join('');
}

