function getRoute(tickets = []) {
  // TODO
  const set = new Set();

  const result = [];
  const map = new Map();

  tickets.forEach((item) => {
    if (!set.has(item.from)) set.add(item.from);
    map.set(item.from, item);
  });

  tickets.forEach((item) => {
    if (set.has(item.to)) set.delete(item.to);
  });


  let i = 0;
  let current = [...set][0];

  while (i < map.size) {
    const cur = map.get(current);
    result.push(cur);
    current = cur.to;
    i++;
  }

  return result;
}


