async function parallelLimit(urls, limit, callback) {
  // TODO
  const result = [];
  const cache = {};
  const set = new Set();

  for (let i = 0; i < urls.length; i++) {
    const promise = fetchUrl(urls[i], i).finally(() => set.delete(promise));
    set.add(promise);

    if (set.size >= limit) {
      await Promise.race(set);
    }
  }

  await Promise.all(set);

  async function fetchUrl(url, index) {
    try {
      if (!cache[url]) {
        const response = await fetch(url);
        result[index] = response;
        cache[url] = response;
      } else {
        result[index] = cache[url];
      }
    } catch (error) {
      result[index] = error;
      cache[url] = error;
    }
  }


  return callback(result);
}```

