function strjoin(splitter, ...strings) {
  // return strings.join(splitter);
  let result = '';
  for (let i = 0; i < strings.length; i++) {
    let current = strings[i];
    if (i === 0) result += `${current}`;
    else result += `${splitter}${current}`;
  }

  return result;
}

