const square = (x) => x * x;
const times2 = (x) => x * 2;
const sum = (a, b) => a + b;

console.log(compose(square, times2)(2) === square(times2(2)));
console.log(compose(square, times2, sum)(3, 4) === square(times2(sum(3, 4))));


function compose(...funcs) {
  return function (...args) {
    return funcs.reduceRight((acc, fn) => {
      return [fn(...acc)];
    }, args)[0];
  };
}


