```function subtractBusinessDays(startDate, n) {
  const date = new Date(startDate); // –ö–æ–ø–∏—Ä—É–µ–º –¥–∞—Ç—É
  let remainingDays = n;

  while (remainingDays > 0) {
    date.setDate(date.getDate() - 1); // –®–∞–≥ –Ω–∞–∑–∞–¥ –Ω–∞ 1 –¥–µ–Ω—å

    const dayOfWeek = date.getDay(); // –ü–æ–ª—É—á–∞–µ–º –¥–µ–Ω—å –Ω–µ–¥–µ–ª–∏
    const isWeekend = dayOfWeek === 0 || dayOfWeek === 6; // –í–æ—Å–∫—Ä–µ—Å–µ–Ω—å–µ (0) –∏–ª–∏ —Å—É–±–±–æ—Ç–∞ (6)

    if (!isWeekend) {
      remainingDays--; // –£–º–µ–Ω—å—à–∞–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–∞–±–æ—á–∏—Ö –¥–Ω–µ–π
    }
  }

  return date;
}

