 * –ö–∞—Ä—Ä–∏—Ä–æ–≤–∞–Ω–∏–µ ‚Äì —ç—Ç–æ —Ç—Ä–∞–Ω—Å—Ñ–æ—Ä–º–∞—Ü–∏—è —Ñ—É–Ω–∫—Ü–∏–π —Ç–∞–∫–∏–º –æ–±—Ä–∞–∑–æ–º, 
 * —á—Ç–æ–±—ã –æ–Ω–∏ –ø—Ä–∏–Ω–∏–º–∞–ª–∏ –∞—Ä–≥—É–º–µ–Ω—Ç—ã –Ω–µ –∫–∞–∫ f(a, b, c), –∞ –∫–∞–∫ f(a)(b)(c), f(a, b)(c), f(a)(b, c).
 */
const curry = (fn) => {
    return function runCurry(...args) {
        if(args.length >= fn.length) {
            return fn(...args)
        } else {
            return function (...nextArgs) {
                return runCurry(...args, ...nextArgs)
            }
        } 

    }
}


