```function splitCamelCase(word) {
    // –ù–∞—Ö–æ–¥–∏–º –∏–Ω–¥–µ–∫—Å –ø–µ—Ä–≤–æ–π –∑–∞–≥–ª–∞–≤–Ω–æ–π –±—É–∫–≤—ã
    let index = -1;
    for (let i = 0; i < word.length; i++) {
        if (word[i] === word[i].toUpperCase()) {
            index = i;
            break;
        }
    }

    // –ï—Å–ª–∏ –∑–∞–≥–ª–∞–≤–Ω–∞—è –±—É–∫–≤–∞ –Ω–∞–π–¥–µ–Ω–∞, —Ä–∞–∑–¥–µ–ª—è–µ–º —Å–ª–æ–≤–æ –∏ –≤—Å—Ç–∞–≤–ª—è–µ–º –ø—Ä–æ–±–µ–ª
    if (index !== -1) {
        return word.slice(0, index) + ' ' + word.slice(index);
    }

    // –ï—Å–ª–∏ –∑–∞–≥–ª–∞–≤–Ω–∞—è –±—É–∫–≤–∞ –Ω–µ –Ω–∞–π–¥–µ–Ω–∞, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –∏—Å—Ö–æ–¥–Ω–æ–µ —Å–ª–æ–≤–æ
    return word;
}```
