function processOrder(store, order) {
  const storeMap = new Map(store.map(item => [item.size, item.quantity]));
  const needed = [];
  const statsMap = new Map();

  for (const {id, size} of order) {
    const [s1, s2] = size;
    let neededSize = null;

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –æ–±–∞ –≤–∞—Ä–∏–∞–Ω—Ç–∞ –≤ –ø–æ—Ä—è–¥–∫–µ –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç–∞
    if ((s2 && storeMap.get(s2) > 0) && (s1 && storeMap.get(s1) > 0)) {
      neededSize = storeMap.get(s2) > storeMap.get(s1) ? s1 : s2
    } else if (s2 && storeMap.get(s2) > 0) {
      neededSize = s2;
    } else if (storeMap.get(s1) > 0) {
      neededSize = s1;
    }

    if (neededSize === null) {
      return false;
    }

    // –£–º–µ–Ω—å—à–∞–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–æ—Å—Ç—É–ø–Ω–æ–π –æ–¥–µ–∂–¥—ã –Ω–∞ —Å–∫–ª–∞–¥–µ –¥–ª—è –≤—ã–¥–∞–Ω–Ω–æ–≥–æ —Ä–∞–∑–º–µ—Ä–∞
    storeMap.set(neededSize, storeMap.get(neededSize) - 1);

    // –î–æ–±–∞–≤–ª—è–µ–º —Ä–∞—Å–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ
    needed.push({id, size: neededSize});

    // –û–±–Ω–æ–≤–ª—è–µ–º —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫—É
    statsMap.set(neededSize, (statsMap.get(neededSize) || 0) + 1);
  }

  // –§–æ—Ä–º–∏—Ä—É–µ–º stats –∏–∑ statsMap
  const stats = [...statsMap.entries()].map(([size, quantity]) => ({size, quantity}));

  return {
    stats,
    assignment: needed
  };
}```

