```function processOrder(store, order) {
    // –°–æ–∑–¥–∞–µ–º –æ–±—ä–µ–∫—Ç –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –¥–æ—Å—Ç—É–ø–∞ –∫ –∫–æ–ª–∏—á–µ—Å—Ç–≤—É –æ–¥–µ–∂–¥—ã –ø–æ —Ä–∞–∑–º–µ—Ä—É
    const inventoryMap = new Map();
    for (const item of store) {
        inventoryMap.set(item.size, item.quantity);
    }

    // –°–æ—Ä—Ç–∏—Ä—É–µ–º –∑–∞–∫–∞–∑—ã, —á—Ç–æ–±—ã —Å–Ω–∞—á–∞–ª–∞ –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞—Ç—å —Ç–µ, —É –∫–æ—Ç–æ—Ä—ã—Ö –º–µ–Ω—å—à–µ –≤–∞—Ä–∏–∞–Ω—Ç–æ–≤ —Ä–∞–∑–º–µ—Ä–æ–≤
    const sortedOrders = [...order].sort((a, b) => a.size.length - b.size.length);

    const assignments = [];

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –¥–ª—è —Ä–∞—Å–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–æ–≤
    function assignSizes(index, currentInventory, currentAssignments) {
        // –ï—Å–ª–∏ –≤—Å–µ –∑–∞–∫–∞–∑—ã –æ–±—Ä–∞–±–æ—Ç–∞–Ω—ã, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º —Ç–µ–∫—É—â–∏–µ –Ω–∞–∑–Ω–∞—á–µ–Ω–∏—è
        if (index === sortedOrders.length) {
            return currentAssignments;
        }

        const orderItem = sortedOrders[index];
        const possibleSizes = orderItem.size;

        for (const size of possibleSizes) {
            if (currentInventory.get(size) && currentInventory.get(size) > 0) {
                // –ù–∞–∑–Ω–∞—á–∞–µ–º —Ç–µ–∫—É—â–∏–π —Ä–∞–∑–º–µ—Ä
                currentInventory.set(size, currentInventory.get(size) - 1);
                currentAssignments.push({ id: orderItem.id, size: size });

                // –†–µ–∫—É—Ä—Å–∏–≤–Ω–æ –Ω–∞–∑–Ω–∞—á–∞–µ–º —Å–ª–µ–¥—É—é—â–∏–π –∑–∞–∫–∞–∑
                const result = assignSizes(index + 1, currentInventory, currentAssignments);
                if (result) {
                    return result;
                }

                // –û—Ç–∫–∞—Ç—ã–≤–∞–µ–º –∏–∑–º–µ–Ω–µ–Ω–∏—è, –µ—Å–ª–∏ –Ω–∞–∑–Ω–∞—á–µ–Ω–∏–µ –Ω–µ —É–¥–∞–ª–æ—Å—å
                currentInventory.set(size, currentInventory.get(size) + 1);
                currentAssignments.pop();
            }
        }

        // –ï—Å–ª–∏ –Ω–∏ –æ–¥–∏–Ω –∏–∑ —Ä–∞–∑–º–µ—Ä–æ–≤ –Ω–µ –ø–æ–¥—Ö–æ–¥–∏—Ç, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º null
        return null;
    }

    // –ù–∞—á–∏–Ω–∞–µ–º —Ä–µ–∫—É—Ä—Å–∏–≤–Ω–æ–µ –Ω–∞–∑–Ω–∞—á–µ–Ω–∏–µ —Å –ø–µ—Ä–≤–æ–≥–æ –∑–∞–∫–∞–∑–∞
    const finalAssignments = assignSizes(0, new Map(inventoryMap), []);

    if (!finalAssignments) {
        console.log("–ù–µ–≤–æ–∑–º–æ–∂–Ω–æ –≤—ã–ø–æ–ª–Ω–∏—Ç—å –≤—Å–µ –∑–∞–∫–∞–∑—ã —Å —Ç–µ–∫—É—â–∏–º –∏–Ω–≤–µ–Ω—Ç–∞—Ä–µ–º.");
        return false;
    }```

