```export const getNumberCharacters = (str) => {
  // –°–æ–∑–¥–∞–µ–º –æ–±—ä–µ–∫—Ç –¥–ª—è –ø–æ–¥—Å—á–µ—Ç–∞ —Å–∏–º–≤–æ–ª–æ–≤
  const count = {};

  // –ü—Ä–æ—Ö–æ–¥–∏–º –ø–æ –∫–∞–∂–¥–æ–º—É —Å–∏–º–≤–æ–ª—É —Å—Ç—Ä–æ–∫–∏
  for (const char of str) {
    count[char] = (count[char] || 0) + 1; // –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º —Å—á–µ—Ç—á–∏–∫ –¥–ª—è —Å–∏–º–≤–æ–ª–∞
  }

  // –§–æ—Ä–º–∏—Ä—É–µ–º —Å—Ç—Ä–æ–∫—É –≤ —Ñ–æ—Ä–º–∞—Ç–µ "–∫–æ–ª–∏—á–µ—Å—Ç–≤–æ*—Å–∏–º–≤–æ–ª"
  return Object.entries(count).map(([char, num]) => `${num}${char}`).join('');
};

