```   const price = [2, 3, 5, 8, 7, 6];

function getIceCream(amount) {

    const map = new Map();  // –î–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –∏–Ω–¥–µ–∫—Å–æ–≤, –∫–æ—Ç–æ—Ä—ã–µ –º–æ–≥—É—Ç —Å–æ—Å—Ç–∞–≤–∏—Ç—å —Å—É–º–º—É

    for (let i = 0; i < price.length; i++) {
        const complement = amount - price[i];  // –ò—Å–∫–æ–º–æ–µ —á–∏—Å–ª–æ –¥–ª—è —Ç–µ–∫—É—â–µ–π —Ü–µ–Ω—ã
        if (map.has(complement)) {
            // –ï—Å–ª–∏ complement –Ω–∞–π–¥–µ–Ω –≤ map, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –∏–Ω–¥–µ–∫—Å—ã
            return [map.get(complement), i];
        }
        // –ï—Å–ª–∏ complement –Ω–µ –Ω–∞–π–¥–µ–Ω, –¥–æ–±–∞–≤–ª—è–µ–º —Ç–µ–∫—É—â—É—é —Ü–µ–Ω—É –∏ –µ–µ –∏–Ω–¥–µ–∫—Å –≤ map
        map.set(price[i], i);
    }

    return [];  // –ï—Å–ª–∏ –Ω–µ –Ω–∞—à–ª–∏ –ø–∞—Ä—ã, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –ø—É—Å—Ç–æ–π –º–∞—Å—Å–∏–≤
