```class EventEmmiter {
    constructor() {
        // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –æ–±—ä–µ–∫—Ç–∞ –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è —Å–æ–±—ã—Ç–∏–π
        this.events = {}
    }

    addEventListener(event, listener) {
        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —Å—É—â–µ—Å—Ç–≤—É–µ—Ç –ª–∏ —Å–ø–∏—Å–æ–∫ —Å–ª—É—à–∞—Ç–µ–ª–µ–π –¥–ª—è –¥–∞–Ω–Ω–æ–≥–æ —Å–æ–±—ã—Ç–∏—è
        if (!this.events[event]) {
            this.events[event] = [] // –ï—Å–ª–∏ –Ω–µ—Ç, —Å–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π
        }
        this.events[event].push(listener) // –î–æ–±–∞–≤–ª—è–µ–º —Å–ª—É—à–∞—Ç–µ–ª—è –≤ —Å–ø–∏—Å–æ–∫

        // –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é, –∫–æ—Ç–æ—Ä–∞—è —É–¥–∞–ª—è–µ—Ç —Å–ª—É—à–∞—Ç–µ–ª—è
        return () => {
            this.events[event] = this.events[event].filter(el => el !== listener)
        }
    }

    dispatchEvent(event, payload) {
        // –ï—Å–ª–∏ –¥–ª—è —Å–æ–±—ã—Ç–∏—è –Ω–µ—Ç —Å–ª—É—à–∞—Ç–µ–ª–µ–π, –Ω–∏—á–µ–≥–æ –Ω–µ –¥–µ–ª–∞–µ–º
        if (!this.events[event]) {
            return
