function getCorrectPath(path) {
    const tripMap = new Map()
    const destination = new Set()

    path.forEach((ticket)=> {
        tripMap.set(ticket.from, ticket)
        destination.add(ticket.to)

        //tripMap.set(ticket.from, ticket.to)
    })

    let start = null;

    for (let ticket of path){
        if (!destination.has(ticket.from)){
            start = ticket.from
            break;
        }
    }

    const route = []

    while(start){
        const next = tripMap.get(start)

        if (destination.has(next)){
           const existingTicket = path.find(ticket => ticket.from === start && ticket.to === next)
           route.push(existingTicket)
        }
        start = next;
    }

    return route;
}```
